'use client';

import { useState, useEffect } from 'react';
import { evaluate } from 'mathjs';
import Display from '@/components/Display';
import ButtonGrid from '@/components/ButtonGrid';
import HistoryPanel from '@/components/HistoryPanel';

export default function Calculator() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [history, setHistory] = useState<string[]>([]);
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Handle button clicks
  const handleButtonClick = (value: string) => {
    if (value === '=') {
      try {
        const evaluatedResult = evaluate(input);
        setResult(evaluatedResult.toString());
        setHistory((prev) => [...prev, `${input} = ${evaluatedResult}`]);
      } catch (error) {
        setResult('Error');
      }
    } else if (value === 'C') {
      setInput('');
      setResult('');
    } else if (value === 'âŒ«') {
      setInput((prev) => prev.slice(0, -1));
    } else {
      setInput((prev) => prev + value);
    }
  };

  // Handle keyboard input
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      const key = event.key;
      if (/[0-9+\-*/.]/.test(key)) {
        handleButtonClick(key);
      } else if (key === 'Enter') {
        handleButtonClick('=');
      } else if (key === 'Backspace') {
        handleButtonClick('âŒ«');
      } else if (key === 'Escape') {
        handleButtonClick('C');
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [input]);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode((prev) => !prev);
    document.documentElement.classList.toggle('dark');
  };

  return (
    <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? 'bg-gray-900' : 'bg-gray-100'}`}>
      <div className="bg-white dark:bg-gray-700 rounded-lg shadow-lg p-6 w-96">
        {/* Dark Mode Toggle */}
        <button
          onClick={toggleDarkMode}
          className="absolute top-4 right-4 p-2 rounded-full bg-gray-200 dark:bg-gray-600"
        >
          {isDarkMode ? 'ðŸŒž' : 'ðŸŒ™'}
        </button>

        {/* Display */}
        <Display input={input} result={result} />

        {/* Buttons */}
        <ButtonGrid onButtonClick={handleButtonClick} />

        {/* History Panel */}
        <div className="mt-4">
          <HistoryPanel history={history} />
        </div>
      </div>
    </div>
  );
}
